// src/TeamDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TeamDashboard.css'; // Create this CSS file for styling

const TeamDashboard = () => {
  const [teamData, setTeamData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Fetch data from the provided API
    axios.get('https://mocki.io/v1/ddb7e0a8-e218-4e36-b1be-b902cdb1c098')
      .then(response => {
        setTeamData(response.data);
      })
      .catch(error => {
        console.error('Error fetching team data:', error);
      });
  }, []);

  const filterMembers = (member) => {
    const fullName = `${member.first_name} ${member.last_name}`;
    return fullName.toLowerCase().includes(searchTerm.toLowerCase());
  };

  return (
    <div className="team-dashboard">
      <div className="team-header">
        <span className="team-name">Team</span>
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <div className="team-body">
        <div className="role-section">
          <h2>Admins</h2>
          {teamData.filter(member => member.role === 'admin' && filterMembers(member)).map((admin) => (
            <div key={admin.id} className="member-tile">
              <img src={admin.img} alt={`${admin.first_name} ${admin.last_name}`} />
              <div className="member-info">
                <span className="member-name">{`${admin.first_name} ${admin.last_name}`}</span>
                <span className="member-email">{admin.email}</span>
              </div>
            </div>
          ))}
        </div>

        <div className="role-section">
          <h2>Members</h2>
          {teamData.filter(member => member.role === 'member' && filterMembers(member)).map((member) => (
            <div key={member.id} className="member-tile">
              <img src={member.img} alt={`${member.first_name} ${member.last_name}`} />
              <div className="member-info">
                <span className="member-name">{`${member.first_name} ${member.last_name}`}</span>
                <span className="member-email">{member.email}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TeamDashboard;
